mapper.insert:
insert into blog_article ( id, article_digest, article_title, create_date, last_article_id, next_article_id, top,
draft, privacy, article_content, article_html_content, user_id, categories_id ) values ( #{id,jdbcType=INTEGER}, #{articleDigest,jdbcType=VARCHAR}, #{articleTitle,jdbcType=VARCHAR}, #{createDate,jdbcType=VARCHAR}, #{lastArticleId,jdbcType=INTEGER}, #{nextArticleId,jdbcType=INTEGER}, #{top,jdbcType=INTEGER},
#{draft,jdbcType=INTEGER}, #{privacy,jdbcType=INTEGER}, #{articleContent,jdbcType=LONGVARCHAR}, #{articleHtmlContent,jdbcType=LONGVARCHAR}, #{userId,jdbcType=INTEGER}, #{categoriesId,jdbcType=INTEGER}
)

mapper.select:
select article.id, article.article_image, article.article_url, article.article_digest, article.article_title, article.create_date, article.user_id, u.name user_name, article.categories_id, categories.name categories_name
from blog_article article left join sys_user u on article.user_id = u.id left join blog_categories categories on article.categories_id = categories.id where 1=1 and article.draft = 0 and article.privacy = 0
and article.categories_id = #{categoriesId,jdbcType=INTEGER}

select article.id, article.article_html_content, article.article_title, article.likes, article.create_date, article.draft, article.privacy, u.name name, article.categories_id, categories.name categories_name
from blog_article article left join sys_user u on article.user_id = u.id left join blog_categories categories on article.categories_id = categories.id
where article.id = #{id,jdbcType=INTEGER}
