mapper.select:
select article.id, article.article_image, article.article_url, article.article_digest, article.article_title, article.create_date, article.user_id, u.name user_name, article.categories_id, categories.name categories_name
from blog_article article left join sys_user u on article.user_id = u.id left join blog_categories categories on article.categories_id = categories.id where 1 = 1
and article.id = #{id,jdbcType=INTEGER}
and article.draft = 0
and article.privacy = 0
and article.categories_id = #{categoriesId,jdbcType=INTEGER}
and article.top = #{top,jdbcType=INTEGER}
order by create_date desc

select article.id, article.article_image, article.article_url, article.article_digest, article.article_title, article.create_date, article.user_id, u.name user_name, article.categories_id, categories.name categories_name
from blog_article article left join sys_user u on article.user_id = u.id left join blog_categories categories on article.categories_id = categories.id left join blog_article_tags bat on bat.article_id = article.id left join blog_tags tags on tags.id = bat.tags_id where 1 = 1
and article.id = #{id,jdbcType=INTEGER}
and article.draft = 0
and article.privacy = 0
and article.categories_id = #{categoriesId,jdbcType=INTEGER}
and article.top = #{top,jdbcType=INTEGER}
and tags.id = #{tagsId,jdbcType=INTEGER}
order by create_date desc

select article.id, article.article_content, article.article_html_content, article.article_title, article.article_image, article.article_url, article.likes, article.create_date, article.draft, article.privacy, u.name name, article.categories_id, categories.name categories_name
from blog_article article left join sys_user u on article.user_id = u.id left join blog_categories categories on article.categories_id = categories.id
where article.id = #{id,jdbcType=INTEGER}
